mapper.insert:
INSERT INTO BASIC_BOARD (
	TITLE
	, WRITER
	, CONTENT
) VALUE (
	#{title}
	, #{writer}
	, #{content}
)

mapper.update:
UPDATE BASIC_BOARD
SET	READ_CNT = READ_CNT + 1
WHERE BOARD_NUM = #{boardNum}

UPDATE BASIC_BOARD
SET
	TITLE = #{title}
	, CONTENT = #{content}
WHERE BOARD_NUM = #{boardNum}

mapper.insert:
INSERT INTO BOARD_REPLY (
	WRITER
	, CONTENT
	, BOARD_NUM
) VALUE (
	#{writer}
	, #{content}
	, #{boardNum}
)
